mapper.insert:
INSERT INTO user (
    user_id, email, name, nickname,
    profile_img_url, role,
    created_at, active, is_election
) VALUES (
             #{userId}, #{email}, #{name}, #{nickname},
             #{profileImgUrl}, #{role},
             #{createdAt}, #{active}, #{isElection}
         )

mapper.update:
UPDATE user
SET active = #{active}
WHERE user_id = #{userId}

UPDATE user
SET
    name = #{name},
    nickname = #{nickname},
    profile_img_url = #{profileImgUrl}
WHERE user_id = #{userId}

UPDATE user
SET is_election = #{isElection}
WHERE user_id = #{userId}
